INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (58, '李绅', 'http://pics.shicimingju.com/zz/41.png?x-oss-process=image/resize,m_lfit,h_200,w_200', '李绅（772年－846年），字公垂。祖籍亳州谯县（今安徽省亳州市谯城区）。唐朝宰相、诗人，中书令<id=59>李敬玄</id>曾孙。 李绅六岁时丧父，随母亲迁居润州无锡。二十七岁时中进士，补国子助教。后历任中书侍郎、尚书右仆射、淮南节度使等职，会昌六年（846年）在扬州逝世，年七十四。追赠太尉，谥号“文肃”。 李绅与<id=55>元稹</id>、<id=54>白居易</id>交游甚密，为新乐府运动的参与者。著有《乐府新题》二十首，已佚。代表作为《悯农》诗两首：“锄禾日当午，汗滴禾下土，谁知盘中餐，粒粒皆辛苦。”《全唐诗》存其诗四卷。', null, '127首', '唐', '2020-01-03 19:56:51');
