INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (105, '颜延之', 'http://pics.shicimingju.com/zz/378.jpg?x-oss-process=image/resize,m_lfit,h_200,w_200', '颜延之（384～456年），字延年，南朝宋文学家。琅邪临沂（今山东临沂）人。曾祖含，右光禄大夫。祖约，零陵<id=103>太守</id>。父显，护军司马。少孤贫，居陋室，好读书，无所不览，文章之美，冠绝当时，与<id=102>谢灵运</id>并称“颜谢”。嗜酒，不护细行，年三十犹未婚娶。', null, '3首', '南北朝', '2020-01-04 01:50:45');
INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (106, '王士祯', 'http://pics.shicimingju.com/zz/727.jpg?x-oss-process=image/resize,m_lfit,h_200,w_200', '王士祯（1634年9月17日—1711年6月26日），原名<id=107>王士禛</id>，字子真，一字贻上，号阮亭，又号渔洋山人，世称王渔洋，谥文简。山东新城（今桓台县）人，常自称济南人。清<id=108>顺治</id>十五年（1658）进士，<id=41>康熙</id>四十三年（1704）官至刑部尚书，颇有政声。清初杰出诗人、文学家，继<id=111>钱谦益</id>之后主盟诗坛，与<id=113>朱彝尊</id>并称“南朱北王”。诗论创“神韵”说，于后世影响深远。早年诗作清丽澄淡，中年转为苍劲。擅长各体，尤工七绝。好为笔记，有《池北偶谈》、《古夫于亭杂录》、《香祖笔记》等。 <id=41>康熙</id>朝书画家宋荦称王士祯“书法高秀似晋人“。近人称其书法为“诗人之书”。博学好古，又能鉴别书画、鼎彝之属，精金石篆刻。', null, '28首', '清', '2020-01-04 01:50:47');
