INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (133, '晏殊', 'http://pics.shicimingju.com/zz/20.jpg?x-oss-process=image/resize,m_lfit,h_200,w_200', '晏殊（991年—1055年2月27日），字同叔，抚州临川人。北宋著名文学家、政治家。 生于<id=134>宋太宗</id>淳化二年（991年），十四岁以神童入试，赐同进士出身，命为秘书省正字，官至右谏议大夫、集贤殿学士、同平章事兼枢密使、礼部刑部尚书、观文殿大学士知永兴军、兵部尚书，宋<id=40>仁宗</id>至和二年（1055年）病逝于京中，封临淄公，谥号元献，世称晏元献。 晏殊以词著于文坛，尤擅小令，风格含蓄婉丽，与其子<id=135>晏几道</id>，被称为“大晏”和“小晏”，又与<id=33>欧阳修</id>并称“晏欧”；亦工诗善文，原有集，已散佚。存世有《珠玉词》、《晏元献遗文》、《类要》残本。', null, '381首', '宋', '2020-01-04 02:19:17');
