INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (189, '杜审言', 'http://pics.shicimingju.com/zz/169.jpg?x-oss-process=image/resize,m_lfit,h_200,w_200', '杜审言（约645-约708），字必简，襄州襄阳人（今湖北襄阳）。后迁河南巩县（今河南巩义），官修文馆直学士。唐高宗咸亨进士，唐中宗时，因与<id=188>张易之</id>兄弟交往，被流放峰州（今越南越池东南）。“诗圣”<id=2>杜甫</id>的祖父。 曾任隰城尉、洛阳丞等小官，累官修文馆直学士，与<id=185>李峤</id>、<id=190>崔融</id>、<id=187>苏味道</id>被称为“文章四友”，是唐代“近体诗”的奠基人之一，作品多朴素自然。其五言律诗，格律谨严。原有集，已散佚，后人辑有《杜审言诗集》。', null, '49首', '唐', '2020-01-04 02:52:15');
