INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (198, '刘长卿', 'http://pics.shicimingju.com/zz/57.jpg?x-oss-process=image/resize,m_lfit,h_200,w_200', '刘长卿(约726—约786)，字文房，汉族，宣城（今属安徽）人，唐代诗人。后迁居洛阳，河间（今属河北）为其郡望。<id=62>唐玄宗</id>天宝年间进士。肃宗至德中官监察御史，后为长洲县尉，因事下狱，贬南巴尉。代宗大历中任转运使判官，知淮西、鄂岳转运留后，又被诬再贬睦州司马。德宗建中年间，官终随州刺史，世称刘随州。 刘长卿生卒年未确论，各名家说法相差甚远，争议十分激烈，一般认为生于公元709-725年间，逝于786-790年年间。刘长卿工于诗，长于五言，自称“五言长城”。《骚坛秘语》有谓：刘长卿最得骚人之兴，专主情景，刘长卿的名作《逢雪宿芙蓉<id=199>山主</id>人》入选中国全日制学校教材。', null, '502首', '唐', '2020-01-04 02:58:42');
