INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (240, '卢纶', 'http://pics.shicimingju.com/zz/55.jpg?x-oss-process=image/resize,m_lfit,h_200,w_200', '卢纶（739-799年），字允言，今保定涿州市卢家场村人。唐代诗人，大历十才子之一。 <id=62>唐玄宗</id>天宝末年举进士，遇乱不第；唐代宗朝又应举，屡试不第。大历六年，经宰相<id=241>元载</id>举荐，授阌乡尉；后由宰相<id=242>王缙</id>荐为集贤学士，秘书省校书郎，升监察御史。出为陕州户曹、河南密县令。之后<id=241>元载</id>、<id=242>王缙</id>获罪，遭到牵连。唐德宗朝，复为昭应县令，出任河中元帅浑瑊府判官，官至检校户部郎中。不久去世。著有《卢户部诗集》。', null, '319首', '唐', '2020-01-04 03:44:38');
