INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (242, '王缙', 'http://pics.shicimingju.com/zz/463.jpg?x-oss-process=image/resize,m_lfit,h_200,w_200', '王缙（700年－781年），字夏卿，河中（今山西永济）人，祖籍太原祁县（今山西祁县），唐朝宰相，尚书右丞<id=61>王维</id>之弟。 王缙出身于河东王氏，少好学，与兄<id=61>王维</id>俱以名闻。举草泽文辞清丽科上第，历拜黄门侍郎，同中书门下平章事，终太子宾客。文笔泉薮，善草隶书，功超<id=243>薛稷</id>。大历十年（775年）<id=241>元载</id>所撰唐赠兵部尚书王忠嗣碑，为其所行书。卒年八十二。王缙遗作不多，散文只有表、碑、册等体，意义不大，诗作与<id=61>王维</id>的风格相似，具有一种平淡清新之美。事迹收录于《金石录》《唐书本传》《述书赋注》。', null, '7首', '唐', '2020-01-04 03:50:23');
