INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (254, '丁谓', null, '丁谓（966-1037），字谓之，后更字公言，两浙路苏州府长洲县人。先后任参知政事（副相）、枢密使、同中书门下平章事（正相），前后共在相位七年。 因作恶太多，丁谓最后被罢相，贬为崖州（今海南省三亚市）司户参军，他的四个儿子、三个弟弟全部被降黜。抄没家产时，从他家中搜得各地的贿赂物品，不可胜纪。景祐四年（1037）闰四月，卒于光州，归葬苏州城西华山习嘉原。', null, '157首', '宋', '2020-01-04 04:11:06');
