INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (278, '光绪', 'http://pics.shicimingju.com/zz/5207.jpg?x-oss-process=image/resize,m_lfit,h_200,w_200', '清德宗爱新觉罗·载湉（1871年8月14日—1908年11月14日），清朝第十一位皇帝，定都北京后的第九位皇帝，在位年号光绪，史称光绪帝。父亲醇亲<id=279>王奕</id>譞，生母叶赫那拉·婉贞为慈禧皇太后亲妹。在位三十四年。', null, '1首', '清', '2020-01-04 05:18:20');
