INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (295, '王微', null, '王微（1600-1647）字修微，小字王冠，称草衣道人，明末扬州人，江南名妓。 王微一生大致可分为两个时期：第一阶段是1600-1623年之间。前期王微浪迹西湖，与名士初结交；1616年以前，王微常常“扁舟载书，往来<id=296>吴会</id>间”，因而“所与游，皆胜流名士”。所谓名士主要有汪然明、<id=297>潘之恒</id>、王晋公、<id=298>董其昌</id>等人。与汪然明的交往使王微逐渐为名士所知。', null, '60首', '明', '2020-01-04 07:05:59');
