INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (322, '张伯端', 'http://pics.shicimingju.com/zz/1464.jpg?x-oss-process=image/resize,m_lfit,h_200,w_200', '张伯端（公元983年—1082年），一说（公元984年—1082年），道教南宗初祖，字平叔，号紫阳、紫阳山人，后改名用成（或用诚）。人称“悟真先生”，传为“紫玄真人”，又尊为“紫阳真人”。临海（今属浙江）人。自幼博览群书，学贯古今中外，涉猎诸种方术。曾中进士，后谪戍岭南。于成都遇仙人（一说此仙人即为刘海蟾）授道，后著书立说，传道天下。 北<id=267>宋元</id>丰五年（公元1082年）仙逝，飞升前留有《尸解颂》一首：“四大欲散，浮云已空，一灵妙有，法界通融”。 张伯端与杏林翠玄真人石泰、道光紫贤真人<id=323>薛式</id>、泥丸翠虚真人<id=320>陈楠</id>、琼炫紫虚真人<id=317>白玉蟾</id>被奉为“全真道南五祖”（“北五祖”为：东华帝君<id=324>王玄</id>甫、正阳帝君<id=325>钟离权</id>、纯阳帝君吕洞宾、纯佑帝君刘海蟾、辅极帝君<id=326>王重</id>阳）。', null, '138首', '宋', '2020-01-04 08:39:04');
