INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (330, '鲍令晖', 'http://pics.shicimingju.com/zz/352.jpg?x-oss-process=image/resize,m_lfit,h_200,w_200', '鲍令晖，南朝女文学家，东海（今山东省临沂市郯城县）人，是著名文学家<id=266>鲍照</id>之妹，出身贫寒，但能诗文。鲍令晖是南朝宋、齐两代唯一留下著作的女文学家。曾有《香茗赋集》传世，今已散佚。另有留传下来：《拟青青河畔草》、《客从远<id=331>方来</id>》、《古意赠今人》、《代葛沙门妻郭小玉诗》等。', null, '27首', '南北朝', '2020-01-04 08:39:16');
