INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (338, '王褒', 'http://pics.shicimingju.com/zz/364.jpg?x-oss-process=image/resize,m_lfit,h_200,w_200', '王褒（前90年—前51年），蜀资中（今四川省资阳市雁江区昆仑乡墨池坝）人。西汉时期著名的辞赋家，与扬雄并称“渊云”。王褒一生留下《洞箫赋》等辞赋16篇、《桐柏真人王君外传》1卷，明末收集有《王谏议集》11篇。', null, '1首', '南北朝', '2020-01-04 08:45:18');
