INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (387, '北朝民歌', 'http://pics.shicimingju.com/zz/75.jpg?x-oss-process=image/resize,m_lfit,h_200,w_200', '北朝民歌是指南北朝时期北<id=388>方文</id>人所创作的作品，其内容丰富，语言质朴，风格粗犷豪迈，主要收录在《<id=389>乐府诗集</id>》中，今存60多首。', null, '1首', '南北朝', '2020-01-04 09:45:03');
