INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (505, '曹松', 'http://pics.shicimingju.com/zz/142.jpg?x-oss-process=image/resize,m_lfit,h_200,w_200', '曹松，唐代晚期诗人。字梦徵。舒州（今安徽潜山）人。生卒年不详。早年曾避乱栖居洪都西山，后依建州刺史<id=506>李频</id>。李死后，流落江湖，无所遇合。光化四年(901)中进士，年已70余，特授校书郎（秘书省正字）而卒。', null, '138首', '唐', '2020-01-04 13:39:31');
INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (506, '李频', null, '李频（818—876），字德新，唐寿昌长汀源（今浙江建德李家镇）人，葬于永乐（今李家），唐代后期诗人。幼读诗书，博览强记，领悟颇多。著有《梨岳集》一卷，附录一卷。 寿昌县令穆君曾游灵栖洞，即景吟诗：“一径入双崖，初疑有几家。行穷人不见，坐久日空斜”。得此四句后稍顿未续。时李频从行，续吟：“石上生灵笋，池中落异花。终须结茅屋，到此学餐霞。”穆君大为赞赏。但此诗根据史学家考证是李频本人所作。', null, '198首', '唐', '2020-01-04 13:39:31');
INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (507, '齐已', 'http://pics.shicimingju.com/zz/144.jpg?x-oss-process=image/resize,m_lfit,h_200,w_200', '<id=508>齐己</id>（863年—937年），出家前俗名胡德生，晚年自号衡岳沙门，湖南长沙宁乡县塔祖乡人，唐朝晚期著名诗僧。<id=508>齐己</id>的一生经历了唐朝和五代中的三个朝代。', null, '1首', '唐', '2020-01-04 13:39:33');
