INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (528, '李益', 'http://pics.shicimingju.com/zz/174.jpg?x-oss-process=image/resize,m_lfit,h_200,w_200', '李益(约750—约830)，唐代诗人，字君虞，祖籍凉州姑臧（今甘肃武威市凉州区），后迁河南郑州。大历四年(769)进士，初<id=529>任郑</id>县尉，久不得升迁，建中四年(783)登书判拔萃科。因仕途失意，后弃官在燕赵一带漫游。以边塞诗作名世，擅长绝句，尤其工于七绝。', null, '184首', '唐', '2020-01-04 14:05:51');
