INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (535, '崔涂', 'http://pics.shicimingju.com/zz/177.png?x-oss-process=image/resize,m_lfit,h_200,w_200', '崔涂[唐]（约公元887年前后在世），字礼山，善音律，尤善长笛，唐朝江南人，今浙江桐庐、建德一带，唐末诗人，生卒年、生平均不详，约公元八八八年前后在世。唐僖宗光启四年（888）进士，《全唐诗》存其诗1卷。他写的最有名的一首诗是《除夜有怀》。', null, '100首', '唐', '2020-01-04 14:05:59');
INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (536, '崔曙', 'http://pics.shicimingju.com/zz/181.jpg?x-oss-process=image/resize,m_lfit,h_200,w_200', '崔曙（？—739年），一作署，宋州宋城县（今河南商丘）人，唐朝状元、诗人。 崔曙自幼失去双亲，可谓备尝人世艰难困苦。开元二十六年（738年），崔曙科举考试中获得进士第一名，又在殿试中作《奉试明堂火珠》诗。<id=62>唐玄宗</id>看后大为赞赏，取为状元，官授河内县县尉。可惜，第二年，崔曙就病故了。崔曙死后只留下一女，名叫“星星”，世人皆以为“曙后一星孤”是谶语。', null, '16首', '唐', '2020-01-04 14:06:00');
INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (537, '薛逢', null, '薛逢（生卒年不详），字陶臣，蒲州河东（今山西永济市）人，唐代诗人。唐武宗会昌元年（841）辛酉科崔岘榜进士第三人。历侍御史、尚书郎。因恃才傲物，议论激切，屡忤权贵，故仕途颇不得意。《全唐诗》收录其诗一卷。《旧唐书》卷一九零，《新唐书》卷二零三皆有传。', null, '78首', '唐', '2020-01-04 14:06:01');
INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (538, '秦韬玉', 'http://pics.shicimingju.com/zz/184.jpg?x-oss-process=image/resize,m_lfit,h_200,w_200', '秦韬玉，生卒年不详，字中明，京兆长安（今陕西西安）人，或云郃阳（今陕西合阳）人，唐代诗人。出生于尚武世家，父为左军军将。少有词藻，工歌吟，却累举不第，后谄附当时有权势的宦官田令孜，充当幕僚，官丞郎，判盐铁。<id=247>黄巢</id>起义军攻占长安后，韬玉从僖宗入蜀，中和二年（882）特赐进士及第，编入春榜。田令孜又擢其为工部侍郎、神策军判官。时人戏为“巧宦”，后不知所终。', null, '37首', '唐', '2020-01-04 14:06:02');
INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (539, '曾公亮', 'http://pics.shicimingju.com/zz/185.jpg?x-oss-process=image/resize,m_lfit,h_200,w_200', '曾公亮（999年－1078年2月27日），字明仲，号乐正，汉族，泉州晋江（今福建泉州市）人。北宋著名政治家、文学家，刑部郎中<id=540>曾会</id>次子。 天圣二年（1024年），曾公亮登进士第，仕<id=40>仁宗</id>、英宗、神宗三朝，历官知县、知州，知府、知制诰、翰林学士、端明殿学士，参知政事，枢密使和同中书门下平章事等。累封鲁国公，卒赠太师、中书令，配享英宗庙廷，赐谥“宣靖”。为昭勋阁二十四功臣之一。 曾公亮与丁度承旨编撰《武经总要》，为中国古代第一部官方编纂的军事科学百科全书。', null, '6首', '宋', '2020-01-04 14:06:03');
INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (540, '曾会', null, '', null, '10首', '宋', '2020-01-04 14:06:03');
INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (541, '裴迪', 'http://pics.shicimingju.com/zz/187.jpg?x-oss-process=image/resize,m_lfit,h_200,w_200', '裴迪（生卒年不详），字、号均不详，唐代诗人，关中（今属陕西）人。官蜀州刺史及尚书省郎。其一生以诗文见称，是盛唐著名的山水田园诗人之一。诗人<id=61>王维</id>的好友。', null, '30首', '唐', '2020-01-04 14:06:05');
INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (542, '西鄙人', 'http://pics.shicimingju.com/zz/192.jpg?x-oss-process=image/resize,m_lfit,h_200,w_200', '西鄙人，意为西北边境人，相传为唐代五言<id=13>民歌</id>《哥舒歌》的作者。这首<id=13>民歌</id>通过对陇右节度使哥舒翰的歌颂，表达了各族人民对和平生活的向往，平朴自然，雄浑粗犷，流传至今。', null, '1首', '唐', '2020-01-04 14:06:06');
INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (543, '朱庆余', 'http://pics.shicimingju.com/zz/196.jpg?x-oss-process=image/resize,m_lfit,h_200,w_200', '<id=443>朱庆馀</id>（生卒年不详），名可久，字庆馀，以字行，越州（今浙江绍兴）人，唐代诗人，喜老庄之道。宝历二年（826）进士，官至秘书省校书郎，见《唐诗纪事》卷四六、《唐才子传》卷六，《全唐诗》存其诗两卷。 曾作《闺意献张水部》作为参加进士考试的“通榜”，增加中进士的机会。据说<id=12>张籍</id>读后大为赞赏，写诗回答他说：“越女新妆出镜心，自知明艳更沉吟。齐纨未足时人贵，一曲菱歌敌万金。”于是<id=443>朱庆馀</id>声名大震。', null, '1首', '唐', '2020-01-04 14:06:08');
