INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (557, '朱弁', null, '朱弁（biàn）（1085～1144）南宋官员、文学家。字少章，号观如居士。婺源（今属江西）人，<id=178>朱熹</id>叔祖，<id=214>太学生</id>出身。建炎元年自荐为通问副使赴金，为金所拘，不肯屈服，拘留十六年始得放归。曾劝<id=19>宋高宗</id>恢复中原，得罪<id=90>秦桧</id>，官终奉议郎。他在留金期间写下了不少怀念故国的诗作，深切婉转，是南宋初期的重要诗人。有《曲洧旧闻》、《风月堂诗话》等传世。', null, '2首', '宋', '2020-01-04 14:18:36');
INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (558, '曹邺', 'http://pics.shicimingju.com/zz/271.jpg?x-oss-process=image/resize,m_lfit,h_200,w_200', '曹邺（816～？），字邺之，桂州（桂林）阳朔人，与晚唐著名诗人<id=559>刘驾</id>、<id=458>聂夷中</id>、<id=560>于濆</id>、<id=561>邵谒</id>、<id=562>苏拯</id>齐名，而以曹邺才颖最佳。曹邺曾担任吏部郎中（唐）、洋州刺史（唐）、祠部郎中（唐）等职务。', null, '115首', '唐', '2020-01-04 14:18:37');
