INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (591, '南朝民歌', 'http://pics.shicimingju.com/zz/360.jpg?x-oss-process=image/resize,m_lfit,h_200,w_200', '南朝民歌盛于南北朝时间，在中国文学史上留下了华彩的一章。南朝民歌大部分保存在（宋）<id=592>郭茂倩</id>所编《<id=389>乐府诗集</id>·清商曲辞》里，主要有吴歌与西曲两类。《西洲曲》是南朝乐府民歌中最长的抒情诗篇，历来被视为南朝乐府民歌的代表作。', null, '17首', '南北朝', '2020-01-04 14:19:13');
INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (592, '郭茂倩', null, '', null, '1首', '宋', '2020-01-04 14:19:13');
INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (593, '汤惠休', 'http://pics.shicimingju.com/zz/362.jpg?x-oss-process=image/resize,m_lfit,h_200,w_200', '汤惠休，南朝宋诗人，字茂远。生卒年不详。早年为僧，人称“惠休上人”。因善于写诗被徐湛之赏识。孝武帝刘骏命其还俗，官至扬州从事史。钟嵘《诗品》作“齐惠休上人”，可能卒于南齐初。', null, '1首', '南北朝', '2020-01-04 14:19:15');
INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (594, '陶宏景', 'http://pics.shicimingju.com/zz/363.png?x-oss-process=image/resize,m_lfit,h_200,w_200', '陶弘景(456年～536年)，字通明，号华阳隐居，人称“山中宰相”，南朝<id=595>梁时</id>丹阳秣陵（今江苏南京）人。中国南朝齐、<id=595>梁时</id>期的道教思想家、医药家、炼丹家、文学家，晚号华阳隐居，卒谥贞白先生。南朝南齐南<id=595>梁时</id>期的道教茅山派代表人物之一。', null, '1首', '南北朝', '2020-01-04 14:19:16');
