INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (666, '屈大均', null, '屈大均（1630—1696），初名邵龙，又名邵隆，号非池，字骚余，又字翁山、介子，号菜圃，汉族，广东番禺人。明末清初著名学者、诗人，与<id=667>陈恭</id>尹、梁佩兰并称“岭南三大家”，有“广东徐霞客”的美称。曾与<id=668>魏耕</id>等进行反清活动。后避祸为僧，中年仍改儒服。诗有<id=1>李白</id>、<id=117>屈原</id>的遗风，著作多毁于<id=121>雍正</id>、<id=207>乾隆</id>两朝，后人辑有《翁山诗外》、《翁山文外》、《翁山易外》、《广东新语》及《四朝成仁录》，合称“屈沱五书”。', null, '20首', '清', '2020-01-04 16:47:07');
