INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (784, '曹摅', null, '曹摅（？—308年），字颜远，曹魏大司马曹休曾孙，卫将军曹肇之孙，谯国谯县（今安徽亳州）人，西晋官员、文学家，官至襄城<id=103>太守</id>、征南司马。曹摅善治地方，爱民如子，担任临淄令期间更被称为“圣君”。其生平见载于《晋书·良吏传》中，<id=785>张隐</id>《文士传》中亦简载其生平。', null, '40首', '魏晋', '2020-01-04 18:24:47');
