INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (792, '萧瑟瑟', 'http://pics.shicimingju.com/zz/4903.jpg?x-oss-process=image/resize,m_lfit,h_200,w_200', '萧瑟瑟（？—1121年），姓萧，名讳不详，小字瑟瑟，国舅大父房之女，萧瑟瑟在姐妹中排行第二。姐夫为贵族耶律挞葛，妹妹则嫁给副都统耶律余睹。辽天祚帝耶律延禧的妃子，封文妃。', null, '2首', '辽', '2020-01-04 18:30:57');
