INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (885, '陈嘉谋', null, '陈嘉谋，字仲询，福建长乐人，名代正德十二年丁丑(1517)进士。历官松阳知县、韶州同知、应天治中、肇庆知府，授广东按察司兵备副使。', null, '1首', '明', '2020-01-04 20:14:08');
