INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (887, '张俞', null, '张俞（《宋史》作张愈），生卒年不详，北宋文学家。字少愚，又字才叔，号白云先生，益州郫（今四川成都郫都区）人，祖籍河东（今山西）。屡举不第，因荐除秘书省校书郎，愿以授父而自隐于家。<id=38>文彦博</id>治蜀，为筑室青<id=564>城山</id>白云溪。著有《白云集》，已佚。', null, '41首', '宋', '2020-01-04 20:14:10');
