INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (891, '孙逖', 'http://pics.shicimingju.com/zz/227.jpg?x-oss-process=image/resize,m_lfit,h_200,w_200', '孙逖（696～761）唐朝大臣、史学家，唐朝潞州涉县（今河北涉县）人。自幼能文，才思敏捷。曾任刑部侍郎、太子左庶子、少詹事等职。有作品《宿云门寺阁》《赠尚书右仆射》《晦日湖塘》等传世。', null, '59首', '唐', '2020-01-04 20:14:15');
