INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (896, '太上隐者', 'http://pics.shicimingju.com/zz/235.jpg?x-oss-process=image/resize,m_lfit,h_200,w_200', '太上隐者，唐代的隐士，隐居于终南山，自称太上隐者，生平不详。', null, '2首', '唐', '2020-01-04 20:14:20');
