INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (898, '晁说之', 'http://pics.shicimingju.com/zz/238.png?x-oss-process=image/resize,m_lfit,h_200,w_200', '晁说之[chaoyuezhi]（1059年—1129年），字以道、伯以，因慕<id=39>司马光</id>之为人，自号景迂生。先世世居澶州（今河南濮阳），钜野（今山东菏泽市巨野县）人。晁迥玄孙。生于宋<id=40>仁宗</id>嘉祐四年，卒于高宗建炎三年，年七十一岁。', null, '918首', '宋', '2020-01-04 20:14:23');
INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (899, '郑会', null, '郑会，南宋人，字<id=900>文谦</id>，一字有极，号亦山，贵溪（今属江西）人。少游<id=178>朱熹</id>、<id=901>陆九渊</id>之门。宁宗嘉定四年（一二一二）进士。十年，擢礼部侍郎。理宗宝庆元年（一二二五）史弥远专政，引疾归里。卒年八十二。有《亦山集》。已佚。清同治《贵溪县志》卷八有传。郑会诗，据《全芳备祖》等书所录，编为一卷。', null, '54首', '宋', '2020-01-04 20:31:08');
