INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (920, '叶采', null, '叶采，字仲圭，号平巖。南宋时期官员，建阳（今福建）人，叶味道之子。明嘉靖《建阳县志》卷一二有传。', null, '2首', '宋', '2020-01-04 20:37:02');
INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (921, '李涉', 'http://pics.shicimingju.com/zz/255.jpg?x-oss-process=image/resize,m_lfit,h_200,w_200', '李涉（约806年前后在世），唐代诗人。字不详，自号清溪子，洛阳（今河南洛阳）人。早岁客梁园，逢兵乱，避地南方，与弟<id=922>李渤</id>同隐庐山香炉峰下。后出山作幕僚。宪宗时，曾任太子通事舍人。不久，贬为峡州（今湖北宜昌）司仓参军，在峡中蹭蹬十年，遇赦放还，复归洛阳，隐于少室。文宗大和（827-835）中，任国子博士，世称“李博士”。著有《李涉诗》一卷。存词六首。', null, '105首', '唐', '2020-01-04 20:37:03');
