INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (926, '曾几', null, '曾几（1085--1166），字吉甫，自号茶山居士。南宋诗人。其先赣州（今江西赣县）人，徙居河南府（今河南洛阳）。历任江西、浙西提刑、秘书少监、礼部侍郎。曾几学识渊博，勤于政事。其诗的特点讲究用字炼句，作诗不用奇字、僻韵，风格活泼流动，咏物重神似。', null, '617首', '宋', '2020-01-04 20:37:09');
INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (927, '戴敏', null, '', null, '11首', '宋', '2020-01-04 20:48:25');
INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (928, '赵嘏', 'http://pics.shicimingju.com/zz/264.jpg?x-oss-process=image/resize,m_lfit,h_200,w_200', '赵嘏，字承佑，楚州山阳（今江苏省淮安市淮安区）人，唐代诗人。约生于宪宗元和元年(806).年轻时四处游历，大和七年预省试进士下第，留寓长安多年，出入豪门以干功名，其间似曾远去岭表当了几年幕府。后回江东,家于润州(今镇江).会昌四年进士及第,一年后东归。会昌末或大中初复往长安，入仕为渭南尉。约宣宗大中六、七年(852、853)卒于任上。', null, '254首', '唐', '2020-01-04 20:48:26');
INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (929, '司马图', null, '唐代诗人', null, '1首', '唐', '2020-01-04 20:53:06');
