INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (941, '何绍基', null, '何绍基（1799-1873），晚清诗人、画家、书法家。 字子贞，号东洲，别号东洲居士，晚号蝯叟。湖南道州（今道县）人。道光十六年进士。咸丰初简四川学政，曾典福建等乡试。历主山东泺源、长沙城南书院。通经史，精小学金石碑版。据《大戴记》考证《礼经》。书法初学<id=219>颜真卿</id>，又融汉魏而自成一家，尤长草书。有《惜道味斋经说》、《东洲草堂诗·文钞》、《说文段注驳正》等著。', null, '2首', '清', '2020-01-04 20:53:19');
INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (942, '陈玉兰', null, '陈玉兰 生卒年不详。唐代吴（今江苏苏州境内）人<id=513>王驾</id>之妻。有《寄夫》诗广为传颂。', null, '1首', '唐', '2020-01-04 20:53:20');
INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (943, '汉无名氏', 'http://pics.shicimingju.com/zz/284.jpg?x-oss-process=image/resize,m_lfit,h_200,w_200', '', null, '54首', '汉', '2020-01-04 20:53:21');
INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (944, '雷震', null, '雷震，宋朝，生平不详。或以为眉州(今四川眉山)人，<id=96>宋宁宗</id>嘉定年间进士。又说是南昌(今属江西)人，<id=945>宋度宗</id>咸淳元年(1265)进士。其诗见《宋诗纪事》卷七十四。', null, '2首', '宋', '2020-01-04 20:53:22');
