INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (956, '林稹', null, '林稹，（明洪武《苏州府志》卷一二作林植，《宋诗纪事小传补正》卷四谓《江苏志》误作林稙），号丹山（《宋诗纪事》卷七四），长洲人（今江苏苏州）人，茂失子。神宗熙宁九年（一0七六）进士，是宋朝有名的才郎，著有《宫词》百首，《千家诗》收《宫词》两首以及《冷泉亭》一首。', null, '1首', '宋', '2020-01-04 21:27:53');
INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (957, '卢梅坡', null, '卢梅坡，宋朝末年人，具体生卒年、生平事迹不详，存世诗作也不多，与<id=637>刘过</id>是朋友，以两首《雪梅》留名千古。', null, '14首', '宋', '2020-01-04 21:27:54');
