INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (968, '顾贞观', null, '顾贞观（1637－1714）清代文学家。原名华文，字远平、华峰，亦作华封，号梁汾，江苏无锡人。明末东林党人顾宪成四世孙。<id=41>康熙</id>五年举人，擢秘书院典籍。曾馆纳兰相国家，与相国子<id=88>纳兰性德</id>交契，<id=41>康熙</id>二十三年致仕，读书终老。贞观工诗文，词名尤著，著有《弹指词》、《积书岩集》等。顾贞观与陈维嵩、<id=113>朱彝尊</id>并称明末清初“词家三绝”，同时又与<id=88>纳兰性德</id>、曹贞吉共享“京华三绝”之誉。', null, '4首', '清', '2020-01-04 21:28:06');
