INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (1021, '罗邺', null, '罗邺(825—?)，字不详，余杭人，有“诗虎”之称。约唐僖宗乾符中前后在世。著有诗集一卷，《新唐书艺文志》传于世。', null, '142首', '唐', '2020-01-04 21:54:27');
INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (1022, '毛熙震', null, '毛熙震[约公元947年前后在世]字不详,蜀人。生卒年均不详,约后晋高祖天福中前后在世。曾为后蜀秘书监。熙震善为词，今存二十九首，（见《唐五代词》）辞多华丽。', null, '13首', '唐', '2020-01-04 21:54:28');
INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (1023, '钱珝', null, '钱珝，字瑞文，吴兴（今浙江湖州市吴兴区）人。《江行无题一百首》，是他赴抚州司马任途中所作。', null, '2首', '唐', '2020-01-04 21:54:29');
INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (1024, '邱为', null, '<id=519>丘为</id>，苏州嘉兴(今属浙江)人。事继母孝，常有灵芝生堂下。累官太子右庶子。初累举不第，归山读书数年，于玄宗天宝二年(743)进士及第。给俸禄之半以终身。年八十馀，母尚无恙。及居忧，观察使<id=520>韩滉</id>以致仕官给禄，所以惠养老臣，不可在丧而异，惟罢春秋羊酒。卒年九十六。与<id=198>刘长卿</id>善，其赴上都也，长卿有诗送之，亦与<id=61>王维</id>为友。诗十三首。', null, '1首', '唐', '2020-01-04 21:54:30');
INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (1025, '戎昱', 'http://pics.shicimingju.com/zz/449.jpg?x-oss-process=image/resize,m_lfit,h_200,w_200', '戎昱（rongyu）（744年～800年），唐代诗人。荆州（今湖北江陵）人，郡望扶风（今属陕西）。少年举进士落第，游名都山川，后中进士。一说登进士第。肃宗上元至代宗永泰(760—766)间，来往于长安、洛阳、齐、赵、泾州、陇西等地。经华阴，遇见<id=688>王季友</id>，同赋《苦哉行》。大历元年(766)入蜀，见<id=122>岑参</id>于成都。大历二年(767)秋回故乡，在荆南节度使卫伯玉幕府中任从事。后流寓湖南，为潭州刺史<id=1026>崔瓘</id>、桂州刺史李昌巙幕僚。建中三年(782)居长安，任侍御史。翌年贬为辰州刺史。后又任虔州刺史。晚年在湖南零陵任职，流寓桂州而终。中唐前期比较注重反映现实的诗人之一。名作《苦哉行》写战争给人民带来灾难。羁旅游宦、感伤身世的作品以《桂州腊夜》较有名。', null, '114首', '唐', '2020-01-04 21:54:31');
