INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (1042, '翁宏', null, '翁宏[约公元九一五年前后在世]字大举，桂岭（今广西贺州市桂岭镇）人。生卒年均不详，不仕。入宋，寓居昭、贺间。与<id=1043>王元</id>、<id=1044>廖融</id>等交游唱和。《宫词》（一作《春残》）等诗最为当时所称。所作诗今存三首。', null, '15首', '宋', '2020-01-04 21:54:50');
