INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (1067, '李重元', null, '李重元，生卒年不详，约1122（宋徽宗宣和）前后在世。南宋<id=643>黄升</id>编《花庵词选》，及《全宋词》收其《忆王孙》词4首，分咏春、夏、秋、冬四季。《婉约词》中收2首。', null, '4首', '宋', '2020-01-04 21:55:17');
INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (1068, '吕蒙正', 'http://pics.shicimingju.com/zz/496.jpg?x-oss-process=image/resize,m_lfit,h_200,w_200', '吕蒙正（公元944年-公元1011年），字圣功，河南洛阳人。北宋初年宰相。 太平兴国二年（公元977年）丁丑科状元。吕蒙正中状元后，授将作监丞，通判升州。后三次登上相位，封许国公，授太子太师。吕蒙正宽厚正直，对上遇礼敢言，对下则宽容有雅度。卒于北宋大中祥符四年（公元1011年），谥文穆，赠中书令。', null, '9首', '宋', '2020-01-04 21:55:18');
INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (1069, '潘阆', null, '潘阆（làng）（?～1009）宋初著名隐士、文人。字梦空，一说字逍遥，号<id=1070>逍遥子</id>，大名（今属河北）人，一说扬州(今属江苏)人。性格疏狂，曾两次坐事亡命。<id=255>真宗</id>时释其罪，任滁州参军。有诗名，风格类<id=223>孟郊</id>、<id=225>贾岛</id>，亦工词，今仅存《酒泉子》十首。', null, '97首', '宋', '2020-01-04 21:55:19');
