INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (1078, '谢克家', null, '谢克家（1063—1134）字任伯，上蔡（今属河南）人。绍圣四年（1097）中进士，宋朝诗人、书法家。词存《忆君王》一首，见《避戎夜话》。《全宋词》辑录。', null, '2首', '宋', '2020-01-04 21:59:21');
