INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (1126, '严蕊', null, '严蕊（生卒不详），原姓周，字幼芳，汉族，生卒年不详，南宋中期女词人。出身低微，自小习乐礼诗书，后沦为台州营妓，改严蕊艺名。 严蕊善操琴、弈棋、歌舞、丝竹、书画，学识通晓古今，诗词语意清新，四方闻名，有不远千里慕名相访。', null, '3首', '宋', '2020-01-04 23:33:37');
INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (1127, '姚宽', null, '姚宽，字令威，号西溪。会稽嵊县（今浙江省嵊县）人，宋宣和3年随父迁居诸暨，其子姚侃、姚仅为诸暨市浬浦镇陶姚村姚氏迁入祖。宋代杰出的史学家、科学家，著名词人。', null, '40首', '宋', '2020-01-04 23:33:38');
INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (1128, '游次公', null, '游次公，字子明，号西池，又号寒岩，建安（今福建建瓯）人，著名理学家<id=1129>游酢</id>侄孙，礼部侍郎游操之子。乾道末，为<id=92>范成大</id>幕僚，多有唱和，又曾为安仁令。淳熙十四年（1187）以奉议郎通判汀州。著有《倡酬诗卷》，存词五首。 <id=640>刘克庄</id>《后村诗话》前集谓其“诗词皆工”，并载其宫词《虞美人》云：“范石湖（成大）座上客有谈刘婕妤者，公与客约赋词，游次公先成，公不复作，众亦敛手。”', null, '10首', '宋', '2020-01-04 23:33:40');
