INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (1153, '折元礼', null, '折元礼：（？-1221）金代词人。字安上。世为麟抚经略使。父折定远，侨居忻州（今山西忻州），遂为忻州人。', null, '1首', '金', '2020-01-04 23:41:56');
