INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (1316, '李从善', 'http://pics.shicimingju.com/zz/769.jpg?x-oss-process=image/resize,m_lfit,h_200,w_200', '李从善（940—987年），字子师，祖籍陇西郡成纪县（今甘肃省天水市），五代时南唐诗人，元宗<id=84>李璟</id>第七子。母凌氏吴国太夫人（一说钟皇后），李后主之弟。南<id=6>唐时</id>，封为郑王，累迁太尉、中书令。 开宝四年春，奉方物来朝贡<id=87>宋太祖</id>，授泰宁军节度、兖海沂等州观察等使。<id=449>赵匡胤</id>特意带着他来到挂画的殿中，见到林仁肇画像，归国后禀告<id=83>李煜</id>，使其猜忌疑林仁肇有二心，并用毒酒鸩杀之。 南唐灭亡后，归顺宋朝，降封南楚国公，改右神武大将军。雍熙初，再迁右千牛卫上将军，出为通许监军。四年，卒，年四十八。', null, '2首', '宋', '2020-01-04 23:44:54');
