INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (1689, '袁瓘', null, '袁瓘，襄阳人，后移居宋州。喜爱剑术，与<id=70>孟浩然</id>，储日羲友善，玄宗时，任左<id=17>拾遗</id>，<id=70>孟浩然</id>到洛阳曾寻访袁瓘，而袁瓘已贬官岭南，孟写诗《洛中访袁<id=17>拾遗</id>不遇》，后来<id=70>孟浩然</id>又到岭南寻访，而袁瓘又遇赦而归授太祝之职，<id=70>孟浩然</id>写诗《南还舟中寄袁太祝》。开元十一年，<id=70>孟浩然</id>在长安与袁瓘重逢，不久，袁瓘调任赣县尉，<id=70>孟浩然</id>写诗《送袁太祝尉豫章》。《全唐诗》存其诗二首。', null, '4首', '宋', '2020-01-04 23:56:36');
