INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (1910, '沈邈', null, '沈邈，字子山，信州弋阳人。进士及第，起家补大理评事、知侯官县，通判广州，累迁都官员外郎，历知真州、福州。', null, '3首', '宋', '2020-01-05 00:00:36');
INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (1911, '杨适1', null, '', null, '1首', '', '2020-01-05 00:00:37');
INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (1912, '滕宗谅', null, '滕宗谅（990年-1047年），字子京，河南洛阳人，北宋官员，因<id=129>范仲淹</id>的《岳阳楼记》而为世人所知，岳阳楼的双公祠中有<id=129>范仲淹</id>与滕子京的雕像（右）。在岳州做过<id=103>太守</id>。他做<id=103>太守</id>时，政事顺利百姓和乐。', null, '15首', '宋', '2020-01-05 00:00:38');
INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (1913, '张昪', null, '张昪（992年—1077年），字杲卿，陕西韩城人。北宋大臣、诗人。大中祥符八年（1015年）进士，官至御史中丞、参知政事兼枢密使，以太子太师致仕。熙宁十年（1077年）卒，年八十六。册赠司徒兼侍中，谥号“康节”。', null, '2首', '宋', '2020-01-05 00:00:39');
INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (1914, '石延年', 'http://pics.shicimingju.com/zz/1443.jpg?x-oss-process=image/resize,m_lfit,h_200,w_200', '石延年（994年～1041年），北<id=310>宋</id>官员、文学家、书法家。字曼卿，一字安仁，南京<id=310>宋</id>城（今河南省商丘市睢阳区）人。 石延年早年屡试不中，<id=310>宋</id><id=255>真宗</id>年间以右班殿直，改太常寺太祝，累迁大理寺丞，官至秘阁校理、太子中允。北<id=310>宋</id>文学家<id=1915>石介</id>以石延年之诗，<id=33>欧阳修</id>之文，杜默之歌称为“三豪”。 <id=310>宋</id><id=40>仁宗</id>康定二年（1041年）二月四日，石延年卒于京师开封，年四十八岁。石曼卿尤工诗，善书法，著有《石曼卿诗集》传世。', null, '89首', '宋', '2020-01-05 00:00:40');
INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (1915, '石介', null, '', null, '180首', '宋', '2020-01-05 00:00:40');
INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (1916, '关咏', null, '关咏（生卒年不详）字永言，宋朝官屯田郎中，知湖州、通州。嘉祐八年（1063），以太常少卿知泉州，改光禄卿、秘书监。事见《宋诗纪事补遗》卷一六。词存《迷仙引》一首。《诗话总龟》卷三三引《古今诗话》：“石曼卿（延年）尝于平阳会中，代作《寄尹师鲁》一篇”，“永言乃增其词为曲，度以《迷仙引》，于是人争歌之”。', null, '1首', '宋', '2020-01-05 00:00:43');
INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (1917, '谢绛', null, '谢绛（994-1039）：字希深，浙江富阳人，北宋文学家、诗人，六部侍郎。以父荫任试秘书省校书郎。北宋大中祥符八年(1015)，登进士甲科，授太常寺奉礼郎、汝阴知县(今属安徽)。', null, '17首', '宋', '2020-01-05 00:00:44');
INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (1918, '贾昌朝', null, '贾昌朝（997年—1065年），字子明。真定获鹿（今河北获鹿）人。北<id=310>宋</id>宰相、训诂学家、文学家、书法家。 <id=310>宋</id><id=255>真宗</id>天禧元年（1017年）赐同进士出身，任为国子监说书。<id=310>宋</id><id=40>仁宗</id>朝，历任天章阁侍讲、参知政事、枢密使、同平章事、判大名府等职。官至左仆射、观文殿大学士、判尚书都省，封魏国公。治平二年（1065年）去世，年六十八。追赠司空兼侍中，谥号“文元”。<id=310>宋</id>英宗亲题其墓碑为“大儒元老之碑”。 贾昌朝一生博学善论。有文集三十卷，今已佚。今存《群经音辨》、《通纪时令》等书。其中《群经音辨》为中国古代第一部多音多义字手册。', null, '5首', '宋', '2020-01-05 00:00:45');
INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (1919, '尹洙', null, '尹洙（1001年—1047年），字师鲁，洛阳（今河南洛阳市）人，北宋散文家，世称河南先生。', null, '39首', '宋', '2020-01-05 00:00:46');
INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (1920, '叶清臣', null, '叶清臣（1000─1049）北宋名臣。字道卿，长洲人（今苏州市）。天圣二年（1024）榜眼。历任光禄寺丞、集贤校理，迁太常丞，进直史馆。论<id=129>范仲淹</id>、<id=1921>余靖</id>以言事被黜事，为<id=40>仁宗</id>采纳，仲淹等得近徙。同修起居注，权三司使。知永兴军时，修复三白渠，溉田六千顷，实绩显著，后人称颂。著作今存《述煮茶小品》等。皇祐元年卒，年五十（一作四十七）。《宋史》、《东都事略》有传。《全宋词》录其词一首。', null, '13首', '宋', '2020-01-05 00:00:47');
INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (1921, '余靖', null, '', null, '143首', '宋', '2020-01-05 00:00:47');
INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (1922, '吴感', null, '吴感，字应之，吴（今苏州）人。天圣二年（1024），省试第一。九年（1031），湖州归安县主簿。应书判拔萃科，入第五等，授江州军事推官。官至殿中丞。', null, '2首', '宋', '2020-01-05 00:00:49');
INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (1923, '荣諲', null, '荣諲，字仲思，济州任城人。', null, '1首', '宋', '2020-01-05 00:00:50');
INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (1924, '陈凤仪', null, '陈凤仪 (?～?) 北<id=1925>宋成</id>都乐妓。与张安道（方平）同时，为其所宠爱。陈凤仪作歌《一络索》赠送他。今存词1首。', null, '1首', '宋', '2020-01-05 00:00:51');
