INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (1926, '解昉', null, '解昉，生卒年均不详，字方叔，曾任苏州司理，存词二首。', null, '2首', '宋', '2020-01-05 00:00:53');
