INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (1942, '刘敞', null, '刘敞（1019—1068）北宋史学家、经学家、散文家。字原父，一作原甫，临江新喻荻斜（今属江西樟树）。庆历六年与弟<id=1943>刘攽</id>同科进士，以大理评事通判蔡州，后官至集贤院学士。与<id=313>梅尧臣</id>、<id=33>欧阳修</id>交往较多。为人耿直，立朝敢言，为政有绩，出使有功。刘敞学识渊博，<id=33>欧阳修</id>说他“自六经百氏古今传记，下至天文、地理、卜医、数术、浮图、老庄之说，无所不通；其为文章尤敏赡”，与弟<id=1943>刘攽</id>合称为北宋二刘，著有《公是集》。', null, '27首', '宋', '2020-01-05 00:01:11');
INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (1943, '刘攽', null, '', null, '4首', '宋', '2020-01-05 00:01:11');
