INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (1945, '裴湘', null, '宋人，字楚老。<id=1946>裴愈</id>子。<id=40>仁宗</id>时内臣。帝御便殿，试进士诗，亦命之赋诗，顷刻而成，左右为之动容。有《肯堂集》。', null, '2首', '宋', '2020-01-05 00:01:14');
INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (1946, '裴愈', null, '', null, '2首', '宋', '2020-01-05 00:01:14');
INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (1947, '滕甫', null, '东阳人。后避宣仁太后父高遵甫讳，改名元发，字达道。生于天禧四年（1020）。皇祐五年（1053）进士。嘉祐四年（1059），官大理评事。召试，授太子中允、集贤校理。历御史中丞、翰林学士知开封府。出守郡。元祐五年（1090），以龙图阁学士知扬州，未至卒，年七十一。', null, '2首', '宋', '2020-01-05 00:01:16');
