INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (1950, '俞紫芝', null, '俞紫芝（？～1086）字秀老。金华（今属浙江）人，寓居扬州（今属江苏）。少有高行，笃信佛教，得其心法，终身不娶不仕。<id=36>王安石</id>晚年居江宁（今江苏南京），俞紫芝与其弟俞子中（字清老）从游，颇得赏识。<id=36>王安石</id>自称晚年门下多佳客（<id=392>李之仪</id>《书俞秀老诗卷后》），俞氏二兄弟便是代表。', null, '6首', '宋', '2020-01-05 00:01:19');
INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (1951, '郑獬', null, '郑獬（1022——1072）字毅夫，号云谷，虔化人，江西宁都梅江镇西门人，因他的祖父前往湖北安<id=1952>陆经</id>商，便寄居于此。商籍人安陆，详载宁都州志，少负售才词章豪伟，宋皇祐壬辰科举人，癸巳状元及第，初试国子监谢启曰，李广才气自谓无双。', null, '443首', '宋', '2020-01-05 00:01:21');
INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (1952, '陆经', null, '', null, '8首', '宋', '2020-01-05 00:09:16');
INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (1953, '强至', null, '强至，字几圣，钱塘（今杭州）人。庆历六年进士，充泗州司理参军，历官浦江、东阳、元城令。熙宁九年卒，年五十五。其子强浚明收集其遗文，编《祠部集》四十卷，<id=45>曾巩</id>为之序，已佚。', null, '835首', '宋', '2020-01-05 00:09:18');
INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (1954, '沈注', null, '', null, '1首', '宋', '2020-01-05 00:24:30');
INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (1955, '蒲宗孟', null, '蒲宗孟（1022-1088年），字传正，阆州新井（今四川南部西南）人。<id=40>仁宗</id>皇祐五年（1053）进士（《郡斋读书志》卷一九《蒲左丞集》），调夔州观察推官。治平年间，发生了水灾和地震。蒲宗孟上书斥责朝中的大臣，后宫和宦官。神宗熙宁元年（1068）召试<id=228>学士院</id>，为馆阁校勘。六年，进集贤校理（《续资治通鉴长编》卷二四六），同修起居注、知制诰，转翰林学士兼侍读。元丰六年（1083），出知汝州，加资政殿学士，徙亳、扬、杭、郓州。哲宗元祐三年（1088），御史劾在郓为政惨酷，夺职知虢州（同上书卷四二七）。', null, '1首', '宋', '2020-01-05 00:24:31');
INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (1956, '陈汝羲', null, '', null, '1首', '宋', '2020-01-05 00:24:32');
