INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (1958, '范纯仁', null, '范纯仁（1027年6月－1101年），字尧夫，谥忠宣。北宋大臣，人称“布衣宰相”。参知政事<id=129>范仲淹</id>次子。 天圣五年（1027年）六月，范纯仁生于南京应天府（今河南商丘）。宋<id=40>仁宗</id>皇祐元年进士。曾从<id=703>胡瑗</id>、<id=1959>孙复</id>学习。父亲殁没后才出仕知襄邑县，累官侍御史、同知谏院，出知河中府，徙成都路转运使。宋哲宗立，拜官给事中，元祐元年同知枢密院事，后拜相。宋哲宗亲政，累贬永州安置。范纯仁于宋徽宗登基后，官复观文殿大学士，后以目疾乞归。建中靖国年间去世，追赠开府仪同三司，谥号忠宣。著有《范忠宣公集》。', null, '1首', '宋', '2020-01-05 00:24:34');
