INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (4873, '汪琬', 'http://pics.shicimingju.com/zz/4586.jpg?x-oss-process=image/resize,m_lfit,h_200,w_200', '汪琬（1624—1691年），字苕文，号钝庵，初号玉遮山樵，晚号尧峰，小字液仙。长洲（今江苏苏州）人，清初官吏学者、散文家，与侯方域、魏禧，合称明末清初散文“三大家”。<id=108>顺治</id>十二年进士，<id=41>康熙</id>十八年举鸿博，历官户部主事、刑部郎中、编修，有《尧峰诗文钞》、《钝翁前后类稿、续稿》。', null, '1首', '清', '2020-01-05 10:45:30');
