INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (4875, '陆次云', null, '诗人，生卒年均不详，约清圣祖<id=41>康熙</id>初前后在世。字云士，号北墅，浙江钱塘人，代表诗作《志感》、《登岱》等。', null, '1首', '清', '2020-01-05 10:45:32');
