INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (4886, '许虬', 'http://pics.shicimingju.com/zz/4602.jpg?x-oss-process=image/resize,m_lfit,h_200,w_200', '许虬（约公元一六六二年前后在世）改姓顾，后复姓许，字竹隐，江苏长洲（苏州）人。卒年六十五。', null, '1首', '清', '2020-01-05 10:45:44');
