INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (4888, '陈曾寿', null, '陈曾寿（1878～1949）晚清官员、诗人。字仁先，号耐寂、复志、焦庵，家藏元代<id=4828>吴镇</id>所画《苍虬图》，因以名阁，自称苍虬居士，湖北蕲水县（今浠水县）巴河陈家大岭人，状元<id=3783>陈沆</id>曾孙。<id=278>光绪</id>二十九年进士，官至都察院广东监察御史，入民国，筑室杭州小南湖，以遗老自居，后曾参与<id=759>张勋</id>复辟、伪满组织等。书学苏东坡，画学<id=267>宋元</id>人。其诗工写景，能自造境界，是近代宋派诗的后起名家，与<id=768>陈三立</id>、<id=4882>陈衍</id>齐名，时称海内三陈。', null, '1首', '清', '2020-01-05 10:45:46');
INSERT INTO gsw.author (id, name, img, summary, masterpiece, number, era, create_time) VALUES (4889, '谢燮', null, '谢燮（525-589），出生于建康（南京市），南朝陈文学家，代表作品《早梅》，和其他咏梅诗不同，这首诗独出心裁，用拟人化的手法描写梅花的不畏严寒和不甘落后，也寄寓着诗人自己的怀才不遇之感。', null, '1首', '南北朝', '2020-01-05 10:45:48');
